INSERT INTO `pages` VALUES (1,'Головна','<div style=\"text-align: center;width:100%;\">\r\n                     <h1>Ласкаво просимо на сторінку Адміністратора!</h1>\r\n                     <img style=\"align:center; height:550px;\" src=\"assets/img/webtools.jpg\"/>\r\n                </div><br><br>','Головна','index.php'),(2,'Помилка','<div style=\"width:100%;text-align:center;\">\r\n                     <h1>Упс! Помилочка! </h1>\r\n                     <img src=\"assets/img/error.jpg\" style=\"height:500px;\"/><br><br>\r\n                     <label style=\"width:100%; text-align: center\">                      \r\n                       <span style=\"color:red;\"><?php echo isset($_GET[\"error\"]) ? $_GET[\"error\"] : \"\"; ?></span>                       \r\n                     </label> \r\n              </div>        \r\n                       <br><br><br><br><br><br>','Помилка','error.php'),(3,'Додати новину',' <form action=\"handler.php\" method=\"post\" enctype=\"multipart/form-data\">\r\n                              <table>\r\n                              <tr>\r\n                              <td>Назва</td>\r\n                              <td><textarea name=\"title\" id=\"title\" cols=\"100\" rows=\"2\" required></textarea></td>\r\n                              </tr>\r\n                              <tr>\r\n                              <td>Короткий опис</td>\r\n                              <td><textarea name=\"description\" id=\"description\"  cols=\"100\" rows=\"2\" required></textarea></td>\r\n                              </tr>\r\n                              <tr>\r\n                              <td>Ключові слова</td>\r\n                              <td><textarea name=\"meta_k\" id=\"meta_k\" cols=\"100\" rows=\"2\" required></textarea></td>\r\n                              </tr><center> <tr>\r\n                              <td>Ключовий опис</td>\r\n                              <td><textarea name=\"meta_d\" id=\"meta_d\" cols=\"100\" rows=\"2\" required></textarea></td>\r\n                              </tr></center>\r\n                              <tr>\r\n                              <td>Автор</td>\r\n                              <td><textarea name=\"author\" id=\"author\" cols=\"100\" rows=\"2\" required></textarea></td>\r\n                              </tr>\r\n                              <tr>\r\n                              <td>Текст статьи</td>\r\n                              <td><textarea name=\"text\" id=\"text\" cols=\"100\" rows=\"20\" required></textarea></td>\r\n                              </tr>\r\n                               <tr>\r\n                              <td>Картинка (370x300)&nbsp;&nbsp;&nbsp;&nbsp;<br>формат .png </td>\r\n                              <td> <input type=\'file\' name=\'myfile\' id=\'myfile\' required></td>\r\n                              </tr>\r\n                              <tr>\r\n                              <td><br><br>\r\n                                <input type=\"hidden\" name=\"handler\"  id=\"handler\" value=\"add_news\"/>\r\n                                <input type=\"submit\" value=\"Додати\"><br><br></td>\r\n                              </tr>\r\n                              </table>                              \r\n               </form><br><br>','Додавання новин','add_news.php'),(4,'Видалити новину','<form action=\"handler.php\" method=\"post\"> \r\n                   <input type=\"hidden\" name=\"handler\"  id=\"handler\" value=\"del_news\"/>\r\n                   <?php \r\n                   $result = mysql_query(\"SELECT title, id, img FROM news\",$db);\r\n                   $myrow = mysql_fetch_array($result);\r\n                   do \r\n                   {\r\n                   printf (\"<p><input name=\'id\' type=\'radio\' value=\'%s\'>\r\n                            <lable>%s</lable></p>\r\n                            <input name=\'img_src\' type=\'hidden\' value=\'%s\'>\",\r\n                            $myrow[\"id\"],$myrow[\"title\"],$myrow[\"img\"]);\r\n                   }\r\n                   while ($myrow = mysql_fetch_array($result));\r\n                   ?>\r\n                   \r\n                   <p><input name=\"submit\" type=\"submit\" value=\"Видалити\"></p>\r\n                   </form><br><br><br><br><br><br><br><br><br><br><br><br><br><br><br><br><br><br>','Видалення новин','del_news.php'),(5,'Редагувати новину','бла бла','Редагування новин','edit_news.php'),(6,'Додати фотографії в новину','бла бла','Додавання фотографій в галерею новин','add_newsPhoto.php'),(7,'Видалити фотографії з галереї новини','Видалити фотографії з новини','Видалення фото з галереї новин','del_newsPhoto.php'),(8,'Додати статтю','','Додавання статей','add_article.php'),(9,'Редагувати статтю','','Редагування статті','edit_articles.php'),(10,'Видалити статтю','','Видалення статті','del_articles.php'),(11,'Додати оголошення','Додати оголошення','Додати оголошення','add_desc.php'),(12,'Редагування оголошення','Редагувати оголошення','Редагувати оголошення','edit_desc.php'),(13,'Видалити оголошення','Видалення оголошення','Видалення оголошення','del_desc.php');

